import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from 'axios';
import Cookies from "js-cookie";
import jwtDecode from 'jwt-decode';

// Get token from localstorage
const initialState = {
    msg: "default message",
    isLoggedIn: false,
    loading: false,
    success: false,
    token: "",
    user: '',
    error: null
}

export const vehicle = createAsyncThunk(
  'user/vehicleDetails',
  async () => {

  const options = {
    method: 'POST',
    url: 'https://vehicle-rc-information.p.rapidapi.com/',
    headers: {
        'content-type': 'application/json',
        'X-RapidAPI-Key': '3c720b5980msh1e4d3f5aeec3f51p14a87bjsn1835be8e708a',
        'X-RapidAPI-Host': 'vehicle-rc-information.p.rapidapi.com'
    },
    data: {
        VehicleNumber: 'MH14CH3077'
    }
    };

    try {
        const response = await axios.post(options);
        console.log(response.data);
        return response.data;
    } catch (error) {
        console.error(error);
    }
    }
)



// Redux action to register a new user
export const registerUser = createAsyncThunk(
  'user/register',
  async (userData) => {
    try {
      const response = await axios.post('http://localhost:8080/api/registerUser', userData);
      return response.data;
    } catch (error) {
      // Handle error and return it using rejectWithValue
      return error;
    }
  }
);


export const login = createAsyncThunk(
  'user/login',
  async (userData) => {
    try {

      const response = await axios.post('http://localhost:8080/api/login', userData);
      Cookies.set('token', response.data.token);
      return response.data;
    } catch (error) {
      return error;
    }
  }
)

// Redux action to Logout
export const logout = createAsyncThunk(
  'user/logout',
   async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/logout');
      return response.data;


    } catch (error) {
      // Handle error and return it using rejectWithValue
      return error;
    }
  }
);



const authSlice = createSlice({
    name: "user",
    initialState,

    reducers : {
        setIsLoggedIn: (state) => {
          state.isLoggedIn = true;
        },
        setUser(state, action) {
          state.user = action.payload;
          state.isLoggedIn = true;
        },
        reset : (state) => {
          state.isLoggedIn = false;
          state.token = '';
        }

    },
    extraReducers: {
        [registerUser.pending]: (state, action) => {
            state.loading = true;
        },
        [registerUser.fulfilled]: (state, action) => {
            state.msg = "Successfull"
            state.loading = false
            state.user = action.payload.user;


        },
        [registerUser.rejected]: (state, {payload: {error, msg}}) => {
            state.loading = false;
            if(error) {
                state.error = error
            }else state.msg = msg
        },
        [login.pending]: (state, action) => {
            state.loading = true;
        },
        [login.fulfilled]: (state, action) => {
            state.msg = "Logged in successfully";
            state.loading = false;
            state.token = action.payload.token;
            state.isLoggedIn = action.payload.success;
            state.success = action.payload.success;
        },
        [login.rejected]: (state, {payload: {error, msg}}) => {
            state.loading = false;
            if(error) {
                state.error = error
            }else state.msg = msg
        },
        [logout.pending]: (state, action) => {
            state.loading = true;
        },
        [logout.fulfilled]: (state, action) => {
            state.msg = "logout Successfull";
            state.loading = false;
        },
        [logout.rejected]: (state, {payload: {error, msg}}) => {
            state.loading = false;
            if(error) {
                state.error = error
            }else state.msg = msg
        },
        [vehicle.pending]: (state) => {
            state.loading = true;
        },
        [vehicle.fulfilled]: (state) => {
            state.msg = "vehicle Details Fetched";
            state.loading = false;

        },
        [vehicle.rejected]: (state, {payload: {error, msg}}) => {
            state.loading = false;
            if(error) {
                state.error = error
            }else state.msg = msg
        },

    }
})




export const { reset, setIsLoggedIn, setUser } = authSlice.actions;
export default authSlice.reducer
